// StaffDetail.js
import React from "react";
import { useLoaderData } from "react-router-dom";
import classes from "./StaffDetail.module.css";

function StaffDetail() {
  const staff = useLoaderData();

  return (
    <section className={classes.detail}>
      {staff.image && (
        <img src={`http://localhost:8000${staff.image}`} alt={staff.fname} />
      )}
      <h2>
        {staff.fname} {staff.lname}
      </h2>
      <p>Position: {staff.position}</p>
      <p>Sex: {staff.sex}</p>
      <p>Date of Birth: {new Date(staff.dob).toLocaleDateString()}</p>
      <p>Salary: {staff.salary}</p>
      <p>Branch No: {staff.branchno}</p>
      <p>Telephone: {staff.telephone}</p>
      <p>Mobile: {staff.mobile}</p>
      <p>Email: {staff.email}</p>
    </section>
  );
}

export default StaffDetail;
