import { useState } from "react";
import { useNavigate } from "react-router-dom";
import classes from "./BranchForm.module.css";

export default function BranchForm({ method, inputData, children }) {
  const [selectedImage, setSelectedImage] = useState(null);
  const navigate = useNavigate();

  async function handleSubmit(event) {
    event.preventDefault();
    const formData = new FormData(event.target);
    if (selectedImage) {
      formData.append("image", selectedImage);
    }

    let url = "http://localhost:8000/api/branches";
    if (method === "PATCH") {
      url += `/${inputData.branchno}`;
    }

    try {
      const response = await fetch(url, {
        method: method,
        body: formData,
      });

      if (response.status === 422) {
        const data = await response.json();
        console.error(data.errors);
        return;
      }

      if (!response.ok) {
        throw new Error("Could not save branch");
      }

      navigate("/branches");
    } catch (error) {
      console.error(error.message);
    }
  }

  return (
    <form id="branch-form" onSubmit={handleSubmit} className={classes.form}>
      <p className="control">
        <label htmlFor="branchno">Branch Number</label>
        <input
          type="text"
          id="branchno"
          name="branchno"
          required
          defaultValue={inputData?.branchno ?? ""}
        />
      </p>

      <p className="control">
        <label htmlFor="street">Street</label>
        <input
          type="text"
          id="street"
          name="street"
          required
          defaultValue={inputData?.street ?? ""}
        />
      </p>

      <p className="control">
        <label htmlFor="city">City</label>
        <input
          type="text"
          id="city"
          name="city"
          required
          defaultValue={inputData?.city ?? ""}
        />
      </p>

      <p className="control">
        <label htmlFor="postcode">Postcode</label>
        <input
          type="text"
          id="postcode"
          name="postcode"
          required
          defaultValue={inputData?.postcode ?? ""}
        />
      </p>

      {/* <p className="control">
        <label htmlFor="image">Image</label>
        <input
          type="file"
          id="image"
          name="image"
          accept="image/*"
          onChange={(event) => setSelectedImage(event.target.files[0])}
        />
      </p> */}

      <div className="form-actions">{children}</div>
      <div className={classes.actions}>
        <button type="button" onClick={() => navigate("..")}>
          Cancel
        </button>
        <button type="submit">Save</button>
      </div>
    </form>
  );
}
