import { Link, useSubmit } from "react-router-dom";
import config from "../config/config";
import classes from "./BranchItem.module.css";

function BranchItem({ branch }) {
  const submit = useSubmit();

  function startDeleteHandler() {
    const proceed = window.confirm("Are you sure?");

    if (proceed) {
      submit(null, { method: "DELETE" });
    }
  }

  console.log(branch.image); // Log the image URL for debugging

  return (
    <article className={classes.branch}>
      {branch.image && (
        <img src={`${config.SERVER_URL}${branch.image}`} alt={branch.street} />
      )}
      <h1>
        {branch.street}, {branch.city}
      </h1>
      <p>{branch.postcode}</p>
      <menu className={classes.actions}>
        <Link to={`/branches/${branch.branchno}/edit`}>Edit</Link>

        <button onClick={startDeleteHandler}>Delete</button>
      </menu>
    </article>
  );
}

export default BranchItem;
