import { Form, useFetcher, useNavigate } from "react-router-dom";
import classes from "./FindBranchItem.module.css"; // Rename the CSS file if needed
import { useEffect } from "react";

function FindBranchItem() {
  const fetcher = useFetcher();
  const navigate = useNavigate();
  const { data, state } = fetcher;

  useEffect(() => {
    if (state === "idle" && data && data.branch) {
      // Navigate to the BranchItem page with the branchno
      navigate(`/branches/${data.branch.branchno}/edit`);
    }
    if (state === "idle" && data && data.message) {
      // Show alert if there is an error message
      window.alert(data.message);
    }
  }, [data, state, navigate]);

  return (
    <fetcher.Form
      method="post"
      action="/branches/search" // This action should match your server endpoint
      className={classes.findBranch}
    >
      <input
        type="text"
        name="branchno"
        placeholder="Enter branch number..."
        aria-label="Branch number"
      />
      <button type="submit">Find Branch</button>
    </fetcher.Form>
  );
}

export default FindBranchItem;
