import {
  Form,
  json,
  redirect,
  useActionData,
  useNavigate,
  useNavigation,
} from "react-router-dom";

import classes from "./BranchForm.module.css";

function BranchForm({ method, branch }) {
  const data = useActionData();
  const navigate = useNavigate();
  const navigation = useNavigation();

  const isSubmitting = navigation.state === "submitting";

  function cancelHandler() {
    navigate("..");
  }

  return (
    <Form method={method} className={classes.form}>
      {data && data.errors && (
        <ul>
          {Object.values(data.errors).map((err) => (
            <li key={err}>{err}</li>
          ))}
        </ul>
      )}
      <p>
        <label htmlFor="street">Street</label>
        <input
          id="street"
          type="text"
          name="street"
          required
          defaultValue={branch && branch.street}
        />
      </p>
      <p>
        <label htmlFor="city">City</label>
        <input
          id="city"
          type="text"
          name="city"
          required
          defaultValue={branch && branch.city}
        />
      </p>
      <p>
        <label htmlFor="postcode">Postcode</label>
        <input
          id="postcode"
          type="text"
          name="postcode"
          required
          defaultValue={branch && branch.postcode}
        />
      </p>
      <p>
        <label htmlFor="image">Image URL</label>
        <input
          id="image"
          type="url"
          name="image"
          required
          defaultValue={branch && branch.image}
        />
      </p>
      <div className={classes.actions}>
        <button type="button" onClick={cancelHandler} disabled={isSubmitting}>
          Cancel
        </button>
        <button disabled={isSubmitting}>
          {isSubmitting ? "Submitting.." : "Save"}
        </button>
      </div>
    </Form>
  );
}

export default BranchForm;

export async function action({ request, params }) {
  const method = request.method;
  const data = await request.formData();
  const branchData = {
    street: data.get("street"),
    city: data.get("city"),
    postcode: data.get("postcode"),
    image: data.get("image"),
  };

  let url = "http://localhost:3000/api/branches";
  if (method === "PATCH") {
    const branchno = params.branchno;
    url = `http://localhost:3000/api/branches/${branchno}`;
  }

  const response = await fetch(url, {
    method: method,
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify(branchData),
  });

  if (response.status === 422) {
    return response;
  }

  if (!response.ok) {
    throw json({ message: "Could not save branch" }, { status: 500 });
  }

  return redirect("/branches");
}
