import React, { useState, useEffect } from "react";

function BranchForm({ branch = {}, method }) {
  const [branchData, setBranchData] = useState(branch);

  useEffect(() => {
    setBranchData(branch);
  }, [branch]);

  async function handleSubmit(event) {
    event.preventDefault();

    try {
      const url = `http://localhost:8000/api/branches/${branchData.branchno}`;
      const response = await fetch(url, {
        method: method || "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(branchData),
      });

      const data = await response.json();
      if (response.ok) {
        // Handle successful submission, like navigating to the updated branch
        window.location.href = `/branches/${data.branch.branchno}`;
      } else {
        alert(data.message || "Failed to process branch.");
      }
    } catch (error) {
      console.error("Error submitting branch:", error);
      alert("An error occurred while processing the branch.");
    }
  }

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Branch No:
        <input
          type="text"
          value={branchData.branchno || ""}
          readOnly={method === "PATCH"}
          onChange={(e) =>
            setBranchData({ ...branchData, branchno: e.target.value })
          }
        />
      </label>
      <label>
        Street:
        <input
          type="text"
          value={branchData.street || ""}
          onChange={(e) =>
            setBranchData({ ...branchData, street: e.target.value })
          }
        />
      </label>
      <label>
        City:
        <input
          type="text"
          value={branchData.city || ""}
          onChange={(e) =>
            setBranchData({ ...branchData, city: e.target.value })
          }
        />
      </label>
      <label>
        Postcode:
        <input
          type="text"
          value={branchData.postcode || ""}
          onChange={(e) =>
            setBranchData({ ...branchData, postcode: e.target.value })
          }
        />
      </label>
      {/* Add other fields as needed */}
      <button type="submit">
        {method === "PATCH" ? "Update Branch" : "Create Branch"}
      </button>
    </form>
  );
}

export default BranchForm;
