// StaffItem.js
import React from "react";
import { Link, useSubmit } from "react-router-dom";
import classes from "./StaffItem.module.css";

function StaffItem({ staff }) {
  const submit = useSubmit();

  function startDeleteHandler() {
    const proceed = window.confirm("Are you sure?");

    if (proceed) {
      submit(null, { method: "DELETE" });
    }
  }

  return (
    <li className={classes.item}>
      {staff.image && (
        <img src={`http://localhost:8000${staff.image}`} alt={staff.fname} />
      )}
      <div>
        <h3>
          {staff.fname} {staff.lname}
        </h3>
        <p>{staff.position}</p>
      </div>
      <menu className={classes.actions}>
        <Link to={`/staffs/${staff.staffno}`}>Details</Link>
        <Link to={`/staffs/edit/${staff.staffno}`}>Edit</Link>
        <button onClick={startDeleteHandler}>Delete</button>
      </menu>
    </li>
  );
}

export default StaffItem;
